import React, { Component } from 'react';

class Counter extends Component {
    state = {
        count: 1,
    };
    formatcount(){
        const {count} = this.state;
        return count === 0 ? 'Zero' : this.state.count;
    }
    render() {
        return (
            <>
                <span>{this.state.count}</span>
                <button>Increment</button>
            </>
        );
    }
}

export default Counter;
